      ldi temp, 0x00
      ldi temp, (1 << MUX0) | (0 << MUX1) | (0 << MUX2)
      out ADMUX, temp

      ldi temp, 0x00
      ldi temp, (1 << DDD5)
      out DDRD, temp

      rjmp  Start

